import{_ as l,c as i,o as e,ag as t}from"./chunks/framework.DWIC34_G.js";const r="/docs/assets/1745463434305.gAdpv9RU.jpg",_=JSON.parse('{"title":"DNS","description":"","frontmatter":{},"headers":[],"relativePath":"network/DNS.md","filePath":"network/DNS.md","lastUpdated":null}'),n={name:"network/DNS.md"};function o(s,a,d,p,h,c){return e(),i("div",null,a[0]||(a[0]=[t('<h1 id="dns" tabindex="-1">DNS <a class="header-anchor" href="#dns" aria-label="Permalink to &quot;DNS&quot;">​</a></h1><p><img src="'+r+'" alt=""></p><h2 id="什么是dns" tabindex="-1">什么是DNS <a class="header-anchor" href="#什么是dns" aria-label="Permalink to &quot;什么是DNS&quot;">​</a></h2><p>Domain Name System 域名解析系统，在网络中提供域名 ↔ ip的应用层解析服务。</p><p>记住一位朋友的名字显然比记住他的身份证号要容易得多。对于一个网站来说ip相当于身份证号，而域名就相当于名字。域名更容易被用户记住，ip更适合计算机处理。</p><h2 id="dns解析过程" tabindex="-1">DNS解析过程 <a class="header-anchor" href="#dns解析过程" aria-label="Permalink to &quot;DNS解析过程&quot;">​</a></h2><p>计算机网络中存在着诸多dns服务器（多级缓存），从小到大：</p><ul><li>应用缓存</li><li>本地host文件</li><li>本地DNS服务器</li><li>根服务器</li><li>顶级服务器</li><li>权威服务器</li></ul><h2 id="域名配置" tabindex="-1">域名配置 <a class="header-anchor" href="#域名配置" aria-label="Permalink to &quot;域名配置&quot;">​</a></h2><ul><li>开发者在域名服务商购买的域名是顶级域名，域名服务商为购买的域名提供对应的解析服务。</li><li>域名解析配置有多种类型 <ul><li>AAAA：ipv6</li><li>A：ipv4</li><li>CNAME：另一个域名</li></ul></li></ul><h2 id="大型分布式应用的dns" tabindex="-1">大型分布式应用的DNS <a class="header-anchor" href="#大型分布式应用的dns" aria-label="Permalink to &quot;大型分布式应用的DNS&quot;">​</a></h2><ul><li>为了响应速度，ip是动态的，会根据运营商、用户所在地等信息将DNS解析到距离用户最近的服务器上。</li><li>根服务不会暴露在公网上，用户能访问到的往往是反向代理服务器、防火墙服务器等。</li></ul>',12)]))}const S=l(n,[["render",o]]);export{_ as __pageData,S as default};
